$(document).ready(function () {
    var paid_temp = {};

    $("form").parent().removeClass();

    $(".submit").on("click", function () {

        var c = [];

        function a() {
            Cookies.remove('paid_temp');
            alert("儲存成功！");
            location.replace("order-checkout.php");
        }

        function b() {
            alert("發生錯誤！");
        }

        var checkedValues = $(".group-check:checked").map(function() {
            return this.value;
        }).get();

        if(checkedValues.length == 0){
            alert("請至少選擇一個店家");
            return false;
        }

        var data = {
            groups: checkedValues
        };

        console.log(data);

        $.redirect('order-checkout.php', data);

        // return myPost("set-checkout-orders", data, a, b, this);

    });

    $("#check-all").click(function () {
        $(".group-check").prop('checked', $(this).prop('checked'));
    });


});

